var title = "Lacuna received packet";
var legend1 = "ls2D";
var legend2 = "ls1";

// variables to be used to 
var i, data1, data2;
var x = msg.payload.x;
var y = msg.payload.y;
var r = msg.payload.r;

// initialise the counter to 0 if it doesn't exist already
var data1 = flow.get('data1')||'';
var data2 = flow.get('data2')||'';


if (msg.payload.sat == '2') {

data1 += "{x: "+x+", y: "+y+", r: "+r+"},";
// this line removes the last comma to end the dataset
}
data1 = data1.replace(/^(.*),(.*?)$/, '$1')

if (msg.payload.sat == '1') {
// for dataset #2
data2 += "{x: "+x+", y: "+y+", r: "+r+"},";

// this line removes the last comma to end the dataset
}
data2 = data2.replace(/^(.*),(.*?)$/, '$1')

// Now we build msg.payload
msg.payload = { "title"   : title,
                "legend1" : legend1,
                "data1"   : data1,
                "legend2" : legend2,
                "data2"   : data2};
data1 += ",";
data2 += ",";

flow.set(["data1", "data2"], [data1, data2]);
return msg;

